import React, {useEffect, useState} from 'react';
import { FlatList, ImageBackground , StyleSheet, Text,View ,TouchableOpacity,ActivityIndicator } from 'react-native';
//import Row from './Row'
import { Ionicons } from '@expo/vector-icons';
import backgroundImage from './assets/cloudy.png'; 
let weatherIcon = "null";
export default function App() { 
  const[weather,setWeather] = useState(null);     //these will be imported from API
  const [isLoaded, setIsLoaded] = useState(false)
  useEffect(() => {
    async function didMount() {
      return fetch("http://api.weatherstack.com/current?access_key=5396596fed7b8184f7d3452029606433&query=Dammam")
        .then(response => {
          return response.json()

        })
        .then(result => {
          setWeather(result); // this is one object not an array
          setIsLoaded(true)
        })

        .catch(error => {
          alert(error.message)
        })
    }
    didMount();

  }, [])
 settingWeather = () =>{
    let currentweather=weather.current.weather_descriptions[0];
  if (currentweather == "Sunny"){
   return weatherIcon="weather-sunny";
  }
  else if (currentweather == "Clear"){
  } 
  else if (currentweather == "Cloudy"){
  } 
  if (!isLoaded) {
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <ActivityIndicator color="#000" />
    </View>
  }

  return (
    <View style={styles.container}>
      <ImageBackground  source={{uri: backgroundImage}} style={styles.testImg}>
        <View style={styles.outercontainer} >
      <View style={styles.col}>
      <View style={styles.row}> 
      {weather && <Text style={[styles.textleftLarge,styles.shadow]}> {weather.current.temperature}</Text>} 
      <Text style={[styles.subfont,styles.shadow]} >Â° C</Text>
      </View> 
      {weather && <Text style={[styles.textleft,styles.subfont]}> {weather.location.name} {weather.location.country}</Text>}
        </View>

        <View style={styles.col}>
       <View style={styles.rowRe}> 

       {weather && <Text style={styles.textright}> <Ionicons name="weatherIcon" size={20} color="white" /> {weather.current.weather_descriptions[0]} </Text>}
        </View>
        {weather && <Text style={[styles.textright,styles.subfont]}>Wind {weather.current.wind_speed} km/h </Text>}
        </View>
        </View>
        <TouchableOpacity
        style={styles.transparentButton} >
        <Text style={styles.subfont}>Detailed info</Text>
      </TouchableOpacity>


      </ImageBackground>
       <View>
       <FlatList 

       data={weather}
       renderItem= {({item})=> (
      <Text style={styles.item}> {item.day}     {item.weather}    {item.degree}</Text>
               )
            } 
          />
      </View>   


       {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

  </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
  testImg: {
    width: 300,
    height: 300,
    marginBottom: 10,

  },
