import React, {useEffect, useState} from 'react';
import { FlatList, Button, ImageBackground , StyleSheet, Text,View ,TouchableOpacity} from 'react-native';
import Row from './Row'
import { Ionicons } from '@expo/vector-icons';
import backgroundImage from './assets/cloudy.png'; 


export default function App() { // export is added here to summerize

  const[weather,setweather] = useState([      //these will be imported frm API
    {day : 'today', weather: 'cloudy', degree:'23/16 C'}, // Days need to be calclated 
    {day : 'Tommorow', weather: 'Suuny', degree:'32/20 C'},
    {day : 'Saturday', weather: 'Sunny', degree:'30/19 C'},
    {day : 'Sunday', weather: 'Rainy', degree:'24/13 C'},
  ]); 

  return (
    <View style={styles.container}>
{/* <View>    <Button style={styles.transparentButton}  title=" Detailed info " />
</View> */}


      <ImageBackground  source={{uri: backgroundImage}} style={styles.testImg}>
        <View style={styles.outercontainer} >
      <View style={styles.col}>
      <View style={styles.row}> 
      <Text style={styles.textleftt}> 23</Text> 
      <Text style={[styles.textleftt,styles.subfont]} >Â° C</Text>
      </View> 
      <Text style={[styles.textleftt,styles.subfont]}> city, country</Text>
        </View>

        <View style={styles.col}>
       <View style={styles.rowRe}> 
       <Text style={styles.textright}> <Ionicons name="cloudy-outline" size={20} color="white" /> Cloudy </Text>
        </View>
        <Text style={[styles.textright,styles.subfont]}>Wind 3 km/h </Text>
        </View>
        </View>
        <View> 
        <TouchableOpacity
        style={styles.transparentButton}
      >
        <Text>Detailed info</Text>
      </TouchableOpacity>

        </View>

      </ImageBackground>
       <View>
       <FlatList 

       data={weather}
       renderItem= {({item})=> (
      <Text style={styles.item}> {item.day}     {item.weather}    {item.degree}</Text>
               )
            } 
          />
      </View>   


       {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

  </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
  testImg: {
    width: 300,
    height: 300,
    marginBottom: 10,

  },
  textright: {
    color: "white",
    fontSize: 18,
    marginHorizontal: 5,
    textAlign:"right"

  },
  textleftt: {
    color: "white",
    fontSize: 18,
    textAlign:"left"
  },
  transparentButton:{ // the color is not changing nor the size , idk why 
    padding: 20,
    borderWidth: 1,
    borderRadius: 10,
    borderColor:"white",
    alignSelf:'center',
    backgroundColor: '  transparent',
    opacity: 0.7

  },


  item: { // i need to expand this 
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor:"#ffffff"
  },
  row: {
    marginTop:30,
    flexDirection:'row',
  },
  rowRe: {
    marginTop:30,
    flexDirection:'row-reverse',
  },
  col: {
    flexDirection:'column',
  },
  subfont: {
    fontSize: 12,
  },
  outercontainer:{
    flexDirection:'row',
    justifyContent: 'space-between',
  }


});
