import React, {useEffect, useState} from 'react';
import { FlatList, Button, ImageBackground , StyleSheet, Text,View } from 'react-native';
import Row from './Row'
import { Ionicons } from '@expo/vector-icons';

export default function App() { // export is added here to summerize

  const[weather,setweather] = useState([      //these will be imported frm API
    {day : 'today', weather: 'cloudy', degree:'23/16 C'}, // Days need to be calclated 
    {day : 'Tommorow', weather: 'Suuny', degree:'32/20 C'},
    {day : 'Saturday', weather: 'Sunny', degree:'30/19 C'},
    {day : 'Sunday', weather: 'Rainy', degree:'24/13 C'},
  ]); 

  return (
    <View style={styles.container}>
      < ImageBackground  source={{uri: "https://i.imgur.com/TkIrScD.png" }} style={styles.testImg}>
      <Ionicons style={styles.textleftt} name="cloudy-outline" size={24} color="white" />
      <View style={styles.textleftt}> 
      <Text> 23</Text> 
      <Text>Â° C</Text>

      </View>
      <Text style={(styles.textleftt,styles.row)}> 23</Text> <Text>Â° C</Text>
      <Text style={styles.textright}> city, country</Text>

       <View> 
       <Text style={styles.textright}>Cloudy </Text>

<Text style={styles.textleftt}>Wind 3 km/h </Text>
        </View>
          <Button style={(styles.button,styles.buttonText)} title="Detailed info " />
      </ImageBackground>
       <View>
       <FlatList 
       data={weather}
       renderItem= {({item})=> (
      <Text style={styles.item}> {item.day}     {item.weather}    {item.degree}</Text>
               )
            } 
          />
      </View>   


       {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

  </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  testImg: {
    width: 305,
    height: 200,
    marginBottom: 10,

  },
  textright: {
    color: '#888',
    fontSize: 20,
    marginHorizontal: 30,
    textAlign:"right"
  },
  textleftt: {
    color: 'white',
    fontSize: 20,
    marginHorizontal: 30,
    textAlign:"left"
  },
  button: { // the color is not changing nor the size , idk why 
    padding: 5,
    borderRadius: 10,
    borderColor:"white",
  },
  buttonText: {
    color: "white"
  },

  item: { // i need to expand this 
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor:"#ffffff"
  },
  row: {
    flexDirection:'row'
  }
});
