import React, {useEffect, useState} from 'react';
import { FlatList, ImageBackground , StyleSheet, Text,View ,TouchableOpacity,ActivityIndicator } from 'react-native';
//import Row from './Row'
import { Ionicons } from '@expo/vector-icons';
import backgroundImage from './assets/cloudy.png'; 


export default function App() { 
  const[weather,setweather] = useState([      //these will be imported frm API
    {day : 'today', weather: 'cloudy', degree:'23/16 C'}, // Days need to be calclated 
    {day : 'Tommorow', weather: 'Suuny', degree:'32/20 C'},
    {day : 'Saturday', weather: 'Sunny', degree:'30/19 C'},
    {day : 'Sunday', weather: 'Rainy', degree:'24/13 C'},
  ]); 
  //API
  const [isLoaded, setIsLoaded] = useState(false)
      useEffect(()=>{
      async function didMount(){
      return fetch("http://api.weatherstack.com/current?access_key=5396596fed7b8184f7d3452029606433&query=Dammam")
    .then(res => {
      return res.json()
       })
       .then(result => {
        setweather(result.results[0]);
        setIsLoaded(true) 
        })
        .catch(error => {
        alert(error.message)
        })
      }  
      didMount();
    },[])

    if(!isLoaded){
      <View style={{flex:1, justifyContent:"center", alignItems:"center"}}>
        <ActivityIndicator color="#000" />
      </View>
    }

  return (
    <View style={styles.container}>
      <ImageBackground  source={{uri: backgroundImage}} style={styles.testImg}>
        <View style={styles.outercontainer} >
      <View style={styles.col}>
      <View style={styles.row}> 
      <Text style={[styles.textleftLarge,styles.shadow]}> 23</Text> 
      <Text style={[styles.subfont,styles.shadow]} >Â° C</Text>
      </View> 
      {weather && <Text style={[styles.textleft,styles.subfont]}> {weather.location.country} {weather.location.name}</Text>}
        </View>

        <View style={styles.col}>
       <View style={styles.rowRe}> 
       <Text style={styles.textright}> <Ionicons name="cloudy-outline" size={20} color="white" /> Cloudy </Text>
        </View>
        <Text style={[styles.textright,styles.subfont]}>Wind 3 km/h </Text>
        </View>
        </View>
        <TouchableOpacity
        style={styles.transparentButton} >
        <Text style={styles.subfont}>Detailed info</Text>
      </TouchableOpacity>


      </ImageBackground>
       <View>
       <FlatList 

       data={weather}
       renderItem= {({item})=> (
      <Text style={styles.item}> {item.day}     {item.weather}    {item.degree}</Text>
               )
            } 
          />
      </View>   


       {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

  </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
  testImg: {
    width: 300,
    height: 300,
    marginBottom: 10,

  },
  textright: {
    color: "white",
    fontSize: 18,
    marginHorizontal: 5,
    textAlign:"right",
  },
  textleft: { 
    color: "white",
    fontSize: 18,
    textAlign:"left",
  },

  textleftLarge: {
    color: "white",
    fontSize: 45,
    textAlign:"left",
  },
  shadow:{
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: {width: -1, height: 1},
    textShadowRadius: 5
  },
  transparentButton:{ // fixed, do i need to add real deailes page? 
    padding: 10,
    borderWidth: 1,
    borderRadius: 30,
    borderColor:"white",
    alignSelf:'center',
    backgroundColor: 'transparent',
    justifyContent: 'space-between',
    marginTop:125 // ask for better way
  },
  item: { // i need to expand this and create the style
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor:"#ffffff",
  },
  row: {
    marginTop:25,
    flexDirection:'row',
  },
  rowRe: {
    marginTop:25,
    flexDirection:'row-reverse',

  },
  col: {
    flexDirection:'column',
    paddingLeft:10,
    paddingRight:10
  },
  subfont: {
    fontSize: 12,
    color:"white"
  },
  outercontainer:{
    flexDirection:'row',
    justifyContent: 'space-between',
  }


});
