import React from 'react';
import { FlatList, Button, ImageBackground , StyleSheet, Text, TouchableOpacity,View, ScrollView } from 'react-native';

export default function App() { // export is added here to summerize

  return (
    <View style={styles.container}>


      <ImageBackground  source={{uri: "https://i.imgur.com/TkIrScD.png" }} style={styles.testImg}>
         <Text style={styles.textleftt}> 23</Text>
         <Text style={styles.textright}> city, country</Text>
       <View> 
         <Text style={styles.textright}>Cloudy Icon</Text>
          <Text style={styles.textright}>Cloudy </Text>
        </View>
          <Text style={styles.textleftt}>Wind 3 km/h </Text>
          <Button title="Detailed info " />
      </ImageBackground>

    <ScrollView> 
    <FlatList style={styles.container}
      data={[
        {key: 'day    status    23/16C'},
        {key: 'day    status    23/16C'},
        {key: 'day    status    23/16C'},
        {key: 'day    status    23/16C'},     
      ]}
      renderItem={({item}) => <Text style={styles.item}>{item.key}</Text>}
    />
    </ScrollView>
  </View>
  );

}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  testImg: {
    width: 305,
    height: 200,
    marginBottom: 10,

  },
  textright: {
    color: '#888',
    fontSize: 20,
    marginHorizontal: 30,
    textAlign:"right"
  },
  textleftt: {
    color: '#888',
    fontSize: 20,
    marginHorizontal: 30,
    textAlign:"left"
  },
  button: {
    backgroundColor: "blue",
    padding: 20,
    borderRadius: 5,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  }, 
  thumbnail: {
    width: 300,
    height: 300,
    resizeMode: "contain"
  },
  item: {
    flex:1,
    padding: 10,
    fontSize: 18,
    height: 44,
  },
});
