import React from 'react';
import {StyleSheet, Text, View, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import weatherIcons from "../icons.json";
import { LinearGradient } from 'expo-linear-gradient';



export default function DetailedInfo(props) {
const forecast=props.route.params.forecast 
  return (
    <ScrollView >
    <LinearGradient colors={['#4c669f', '#3b5998', '#192f6a']} style={{ flex:1 }}>


      <View style={styles.container}>

      {forecast && <Text style={styles.mainText}> 
      <Ionicons name={weatherIcons[forecast.current.weather[0].icon].icon} size={100} color="white" /> 
      {forecast.current.weather[0].main} </Text> } 


      <View style={styles.row}> 
      {forecast && <Text style={styles.textleft}> <Ionicons name="sunny" size={20} color="white" /> Sun Rise 
       {new Date((forecast.current.sunrise)* 1000).toLocaleString('en-us',{ hour:'2-digit',minute:'2-digit'}) } </Text> }
       {forecast && <Text style={styles.textRight}><Ionicons name="moon" size={20} color="white" />
     {new Date((forecast.current.sunset)* 1000).toLocaleString('en-us',{ hour:'2-digit',minute:'2-digit'})} </Text> }
     </View>



      </View>
      </LinearGradient>
      </ScrollView>
  );

}


const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },

  mainText: {
    color: "white",
    fontSize: 30,
    textAlign: "center",
  },
  textleft: {
    color: "white",
    fontSize: 20,
    textAlign: "left",
  },
  textRight: {
    color: "white",
    fontSize: 20,
    textAlign: "right",
  },

  textcenterLarge: {
    color: "white",
    fontSize: 45,
    textAlign: "center",
  },
  shadow: {
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 5
  },
  transparentButton: { // fixed, do i need to add real deailes page? 
    padding: 10,
    borderWidth: 1,
    borderRadius: 30,
    borderColor: "white",
    alignSelf: 'center',
    backgroundColor: 'transparent',
    justifyContent: 'space-between',
    marginTop: 230, // ask for better way
    flexDirection: 'center',

  },
  item: { // i need to expand this and create the style
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor: "#ffffff",
  },
  row: {
    marginTop: 25,
    flexDirection: 'row',
  },
  rowRe: {
    marginTop: 40,
    flexDirection: 'row-reverse',

  },
  col: {
    flexDirection: 'column',
    paddingLeft: 10,
    paddingRight: 10
  },
  subfont: {
    fontSize: 12,
    color: "white"
  },
  outercontainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',

  },

  allweather: {
  fontSize:18,
  margin:20
  },

  horizontalLine: {
    borderBottomColor: 'gray',
    borderBottomWidth: 0.25,
  },


});
