import React, { useEffect, useState } from 'react';
import * as Location from 'expo-location';
import { FlatList, ImageBackground, StyleSheet, Text, View, TouchableOpacity, ActivityIndicator, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import backgroundImage from './assets/cloudy.png';
//import Row from './Row'
//import moment from 'moment';
//var start_date = moment().format("YYYY-MM-DD");
//var end_date = moment(7, 'days').format("YYYY-MM-DD");
const access_key = "da4ff156937c474d64069a4fe689ac4c";
//const city = "Dammam";
const lat = "26.4344";
const lon = "50.1033";
//var weatherIcon = "";


export default function App() {
  const [forcecast, setWeather] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false)


  useEffect(() => {
    async function didMount() {

      const { status } = await Location.requestPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission to access location was denied');
      }
      let location = await Location.getCurrentPositionAsync({ enableHighAccuracy: true });

      return fetch(`https://api.openweathermap.org/data/2.5/onecall?l&units=metric&exclude=minutely&lat=${location.coords.latitude}&lon=${location.coords.longitude}&appid=da4ff156937c474d64069a4fe689ac4c`)
        .then(response => {
          return response.json()

        })
        .then(result => {
          setWeather(result);
          /*  var prefix = 'wi wi-';
           var code = result.current.weather[0].icon;
           var icon = weatherIcons[code].icon;

           // If we are not in the ranges mentioned above, add a day/night prefix.
           if (!(code > 699 && code < 800) && !(code > 899 && code < 1000)) {
             icon = 'day-' + icon;
           }

           // Finally tack on the prefix.
           icon = prefix + icon; */
          setIsLoaded(true)
        })

        .catch(error => {
          alert(error.message)
        })


    }

    didMount();

  }, [])

  if (!isLoaded) {
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <ActivityIndicator color="#000" />
    </View>
  }


  return (
    <View style={styles.container}>
      <ImageBackground source={{ uri: backgroundImage }} style={styles.testImg}>
        <View style={styles.outercontainer} >
          <View style={styles.col}>
            <View style={styles.row}>
              {forcecast && <Text style={[styles.textleftLarge, styles.shadow]}> {Math.round(forcecast.current.temp)}</Text>}
              <Text style={[styles.subfont, styles.shadow]} >° C </Text>
            </View>
            <Text style={[styles.textleft, styles.subfont]}> city,country</Text>
          </View>

          <View style={styles.col}>
            <View style={styles.rowRe}>
              {forcecast && <Text style={styles.textright}> <Ionicons name="A" size={20} color="white" /> {forcecast.current.weather[0].main} </Text>}
            </View>
            {forcecast && <Text style={[styles.textright, styles.subfont]}>Wind {forcecast.current.wind_speed} km/h </Text>}
          </View>
        </View>
        <TouchableOpacity
          style={styles.transparentButton} >
          <Text style={styles.subfont}>Detailed info</Text>
        </TouchableOpacity>


      </ImageBackground>
      <View>
        {forcecast.daily[0].slice(0, 5).map(d => {
          const weather = d.weather[0];
          var dt = new Date(d.dt * 1000);
          return <View key={d.dt}>
            <Text> {Math.round(d.temp.max)}°C</Text>
            <View>
              <Text>{dt.toLocaleDateString()}</Text>
              <Text>{weather.description}</Text>
            </View>
          </View>
          })}
     </View>
      {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

    </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
  testImg: {
    width: 300,
    height: 300,
    marginBottom: 10,

  },
  textright: {
    color: "white",
    fontSize: 18,
    marginHorizontal: 5,
    textAlign: "right",
  },
  textleft: {
    color: "white",
    fontSize: 18,
    textAlign: "left",
  },

  textleftLarge: {
    color: "white",
    fontSize: 45,
    textAlign: "left",
  },
  shadow: {
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 5
  },
  transparentButton: { // fixed, do i need to add real deailes page? 
    padding: 10,
    borderWidth: 1,
    borderRadius: 30,
    borderColor: "white",
    alignSelf: 'center',
    backgroundColor: 'transparent',
    justifyContent: 'space-between',
    marginTop: 125 // ask for better way
  },
  item: { // i need to expand this and create the style
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor: "#ffffff",
  },
  row: {
    marginTop: 25,
    flexDirection: 'row',
  },
  rowRe: {
    marginTop: 25,
    flexDirection: 'row-reverse',

  },
  col: {
    flexDirection: 'column',
    paddingLeft: 10,
    paddingRight: 10
  },
  subfont: {
    fontSize: 12,
    color: "white"
  },
  outercontainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  }


});
