import React, { useEffect, useState } from 'react';
import * as Location from 'expo-location';
import moment from 'moment';
import { FlatList, ImageBackground, StyleSheet, Text, View, TouchableOpacity, ActivityIndicator, ScrollView } from 'react-native';
//import Row from './Row'
import { Ionicons } from '@expo/vector-icons';
import backgroundImage from './assets/cloudy.png';
//var start_date = moment().format("YYYY-MM-DD");
//var end_date = moment(7, 'days').format("YYYY-MM-DD");
const access_key = "da4ff156937c474d64069a4fe689ac4c";
//const city = "Dammam";
const lat = "26.4344";
const lon = "50.1033";
//var weatherIcon = "";

/* const DATA = [
  {
    [daily[0].weather[0].main],
    [daily[0].temp[0].day]
  } // fix this 
]; */

export default function App() {
  const [weather, setWeather] = useState(null);     //these will be imported from API
  const [isLoaded, setIsLoaded] = useState(false)


  useEffect(() => {
    async function didMount() {
      const { status } = await Location.requestPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission to access location was denied');
      }
      let location = await Location.getCurrentPositionAsync({ enableHighAccuracy: true });
      //return fetch("https://api.openweathermap.org/data/2.5/onecall?lat=26.4344&lon=50.1033&appid=da4ff156937c474d64069a4fe689ac4c")
      return fetch(`https://api.openweathermap.org/data/2.5/onecall?l&lat=${location.coords.latitude}&lon=${location.coords.longitude}&appid=da4ff156937c474d64069a4fe689ac4c`)
        .then(response => {
          return response.json()

        })
        .then(result => {
          setWeather(result); // this is one object not an array
          /*  var prefix = 'wi wi-';
           var code = result.current.weather[0].icon;
           var icon = weatherIcons[code].icon;

           // If we are not in the ranges mentioned above, add a day/night prefix.
           if (!(code > 699 && code < 800) && !(code > 899 && code < 1000)) {
             icon = 'day-' + icon;
           }

           // Finally tack on the prefix.
           icon = prefix + icon; */
          setIsLoaded(true)
        })

        .catch(error => {
          alert(error.message)
        })


    }

    didMount();

  }, [])

  if (!isLoaded) {
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <ActivityIndicator color="#000" />
    </View>
  }


  return (
    <View style={styles.container}>
      <ImageBackground source={{ uri: backgroundImage }} style={styles.testImg}>
        <View style={styles.outercontainer} >
          <View style={styles.col}>
            <View style={styles.row}>
              {weather && <Text style={[styles.textleftLarge, styles.shadow]}> {((5 / 9) * (parseInt(weather.current.temp) - 32)).toFixed(0)}</Text>}
              <Text style={[styles.subfont, styles.shadow]} >Â° C </Text>
            </View>
            <Text style={[styles.textleft, styles.subfont]}> city,country</Text>
          </View>

          <View style={styles.col}>
            <View style={styles.rowRe}>

              {weather && <Text style={styles.textright}> <Ionicons name="A" size={20} color="white" /> {weather.current.weather[0].main} </Text>}
            </View>
            {weather && <Text style={[styles.textright, styles.subfont]}>Wind {weather.current.wind_speed} km/h </Text>}
          </View>
        </View>
        <TouchableOpacity
          style={styles.transparentButton} >
          <Text style={styles.subfont}>Detailed info</Text>
        </TouchableOpacity>


      </ImageBackground>
      <View>

        <FlatList
          data={weather}
          keyExtractor={(item, index) => index.toString()}
          renderItem={({ item, index }) => {
            <View>

              <Text style={styles.item}>
                {item.daily[0].weather[0].main}
                {iitem.daily[0].weather[0].main}
                {item.daily[0].temp[0].day}
              </Text>
            </View>
          }
          }
        />
      </View>

      {/* renderItem ={({obj})} => (Row {...(obj.item)} ) */}

    </View>
  );

}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
  },
  testImg: {
    width: 300,
    height: 300,
    marginBottom: 10,

  },
  textright: {
    color: "white",
    fontSize: 18,
    marginHorizontal: 5,
    textAlign: "right",
  },
  textleft: {
    color: "white",
    fontSize: 18,
    textAlign: "left",
  },

  textleftLarge: {
    color: "white",
    fontSize: 45,
    textAlign: "left",
  },
  shadow: {
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 5
  },
  transparentButton: { // fixed, do i need to add real deailes page? 
    padding: 10,
    borderWidth: 1,
    borderRadius: 30,
    borderColor: "white",
    alignSelf: 'center',
    backgroundColor: 'transparent',
    justifyContent: 'space-between',
    marginTop: 125 // ask for better way
  },
  item: { // i need to expand this and create the style
    padding: 15,
    fontSize: 14,
    height: 44,
    backgroundColor: "#ffffff",
  },
  row: {
    marginTop: 25,
    flexDirection: 'row',
  },
  rowRe: {
    marginTop: 25,
    flexDirection: 'row-reverse',

  },
  col: {
    flexDirection: 'column',
    paddingLeft: 10,
    paddingRight: 10
  },
  subfont: {
    fontSize: 12,
    color: "white"
  },
